import react from "react";

export default class extends React.Component {
  render() {
    return (
      <div>
        <h1>Hola Mundo!</h1>
        <p>Bienvenidos al curso de next.js</p>

        <img src="/static/platzi-logo.png" alt="PlatziLogo" />

        <style jsx>
          {`
            h1 {
              color: red;
            }
            p {
              color: green;
            }
            img {
              max-width: 50%;
              display: block;
              margin: 0 auto;
            }
          `}
        </style>
        <style jsx global>
          {`
            body {
              background: white;
            }
          `}
        </style>
      </div>
    );
  }
}
