import React from "react";

export default class extends React.Component {
  render() {
    return (
      <div>
        <img src="/static/platzi-logo.png" alt="PlatziLogo" />
        <h1>Creado por Pablo</h1>
        <p>Curso de Next.js de Platzi</p>
        <style jsx global>
          {`
            body {
              background: blue;
            }
          `}
        </style>
        <style jsx>
          {`
            img {
              maxwidth: 120px;
              display: block;
              margin: 0;
            }
            h1 {
              color: white;
              aligncontent: "center";
            }
            p {
              color: white;
              aligncontent: "center";
            }
          `}
        </style>
      </div>
    );
  }
}
